import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  Typography,
  Paper,
  Accordion,
  AccordionSummary,
  AccordionDetails
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import axios from 'axios';

/* ----------------------------------------------------------------------------------
   DataTable – unchanged visual layout, small tweak: prettify arrays & objects
-----------------------------------------------------------------------------------*/
function DataTable({ data, title }) {
  if (!data || data.length === 0) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No data found for {title}
      </Typography>
    );
  }

  const allKeys = new Set();
  data.forEach((item) => Object.keys(item).forEach((k) => allKeys.add(k)));
  const columns = Array.from(allKeys);

  const renderValue = (v) => {
    if (Array.isArray(v)) return v.join(', ');
    if (v && typeof v === 'object') return JSON.stringify(v);
    return v != null ? String(v) : '';
  };

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Box sx={{ overflowX: 'auto', border: '1px solid #ddd' }}>
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              {columns.map((col) => (
                <th
                  key={col}
                  style={{
                    padding: '8px',
                    background: '#f0f0f0',
                    borderBottom: '1px solid #ccc',
                    textAlign: 'left'
                  }}
                >
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {columns.map((col) => (
                  <td
                    key={col}
                    style={{
                      padding: '8px',
                      borderBottom: '1px solid #eee',
                      verticalAlign: 'top'
                    }}
                  >
                    {renderValue(row[col])}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </Box>
    </Box>
  );
}

// ---------------------------------------------------------------------------
// Helper – map Mongo _id ➜ id so the merged table keeps a single id field
// ---------------------------------------------------------------------------
function normaliseId(obj) {
  return obj && obj._id !== undefined && obj.id === undefined
    ? { ...obj, id: obj._id }
    : obj;
}

// Fields to display in merged results.
const MERGED_COLUMNS = [
  'id',
  'name',
  'price',
  'room_type',
  'listing_url',
  'description'
];

function filterColumns(dataArray, desiredFields) {
  if (!Array.isArray(dataArray)) return [];
  return dataArray.map((item) => {
    const src = normaliseId(item);
    const out = {};
    desiredFields.forEach((f) => {
      if (src[f] !== undefined) out[f] = src[f];
      else if (f === 'id' && src['_id'] !== undefined) out[f] = src['_id'];
    });
    return out;
  });
}

/* ==================================================================================
   MAIN APP COMPONENT – same layout, only data‑massaging tweaks
==================================================================================*/
function App() {
  const [operation, setOperation] = useState('query');
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [showRawResults, setShowRawResults] = useState(true);
  const [showMySQL, setShowMySQL] = useState(true);
  const [showMongo, setShowMongo] = useState(true);
  const [showFirebase, setShowFirebase] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const url = `http://localhost:8000/${operation === 'query' ? 'query' : 'modify'}`;
      const payload =
        operation === 'query'
          ? { query, db_type: database }
          : { modification: query };
      const { data } = await axios.post(url, payload);
      setResult(JSON.stringify(data, null, 2));
    } catch (err) {
      setResult('Error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setResult('');
    setQuery('');
  };

  // -------------------- parse backend JSON safely
  let converted = null,
    results = null;
  try {
    const parsed = result ? JSON.parse(result) : null;
    if (parsed) {
      converted = operation === 'query' ? parsed.converted_queries : parsed.converted_modifications;
      results = parsed.results;
    }
  } catch {}

  const { mysql, mongodb, firebase, merged } = results || {};

  const mergedFiltered = Array.isArray(merged) ? filterColumns(merged, MERGED_COLUMNS) : [];
  const hasMySQL = Array.isArray(mysql) && mysql.length > 0;
  const hasMongoDB = Array.isArray(mongodb) && mongodb.length > 0;
  const hasFirebase = firebase && (Array.isArray(firebase) ? firebase.length > 0 : Object.keys(firebase || {}).length > 0);

  const finalOutput = mergedFiltered.length
    ? mergedFiltered
    : hasFirebase
    ? firebase
    : hasMySQL
    ? mysql
    : hasMongoDB
    ? mongodb
    : [];

  /* -------------------- JSX (unchanged visual structure) -------------------- */
  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Form */}
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          ChatDB ‑ Natural Language Interface
        </Typography>
        <form onSubmit={handleSubmit}>
          {/* Operation selector */}
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Operation Type</InputLabel>
              <Select value={operation} label="Operation Type" onChange={(e) => setOperation(e.target.value)}>
                <MenuItem value="query">Query</MenuItem>
                <MenuItem value="modify">Modification</MenuItem>
              </Select>
            </FormControl>
          </Box>
          {/* DB selector (queries only) */}
          {operation === 'query' && (
            <Box sx={{ mb: 2 }}>
              <FormControl fullWidth>
                <InputLabel>Database</InputLabel>
                <Select value={database} label="Database" onChange={(e) => setDatabase(e.target.value)}>
                  <MenuItem value="mysql">MySQL</MenuItem>
                  <MenuItem value="mongodb">MongoDB</MenuItem>
                  <MenuItem value="firebase">Firebase</MenuItem>
                </Select>
              </FormControl>
            </Box>
          )}
          {/* NL input */}
          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label={operation === 'query' ? 'Enter your query in natural language' : 'Enter your modification command'}
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={operation === 'query'
                ? 'e.g., Show me listings with >300 reviews and price < $200'
                : "e.g., Update listing 28138873 set price to $120.00"}
            />
          </Box>
          {/* buttons */}
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button variant="contained" type="submit" disabled={loading || !query} fullWidth>
              {loading ? 'Processing…' : operation === 'query' ? 'Execute Query' : 'Execute Modification'}
            </Button>
            <Button variant="outlined" color="error" onClick={handleClear} disabled={loading || (!query && !result)} fullWidth>
              Clear All
            </Button>
          </Box>
        </form>
      </Paper>

      {/* ---------- RESULTS ---------- */}
      {operation === 'query' && result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Final Output (Merged or Single Source)
          </Typography>
          {Array.isArray(finalOutput) && finalOutput.length ? (
            <DataTable data={finalOutput} title="Final Output" />
          ) : (
            <Typography variant="body2">No data available.</Typography>
          )}
        </Paper>
      )}

      {/* raw converted */}
      {result && (
        <Box sx={{ mb: 2, display: 'flex', justifyContent: 'flex-end' }}>
          <Button variant="text" onClick={() => setShowRawResults(!showRawResults)}>
            {showRawResults ? 'Hide Converted Data' : 'Show Converted Data'}
          </Button>
        </Box>
      )}
      {result && showRawResults && converted && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            {operation === 'query' ? 'Converted Queries' : 'Converted Modification Commands'}
          </Typography>
          <pre style={{ backgroundColor: '#f5f5f5', padding: '8px', overflowX: 'auto' }}>
            {JSON.stringify(converted, null, 2)}
          </pre>
        </Paper>
      )}

      {/* Individual DB accordions (query mode) */}
      {operation === 'query' && result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Individual Database Results
          </Typography>
          <Accordion expanded={showMySQL} onChange={() => setShowMySQL(!showMySQL)}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}><Typography>MySQL Results</Typography></AccordionSummary>
            <AccordionDetails>
              {hasMySQL ? <DataTable data={mysql.map(normaliseId)} title="MySQL" /> : <Typography>No results.</Typography>}
            </AccordionDetails>
          </Accordion>
          <Accordion expanded={showMongo} onChange={() => setShowMongo(!showMongo)}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}><Typography>MongoDB Results</Typography></AccordionSummary>
            <AccordionDetails>
              {hasMongoDB ? <DataTable data={mongodb.map(normaliseId)} title="MongoDB" /> : <Typography>No results.</Typography>}
            </AccordionDetails>
          </Accordion>
          <Accordion expanded={showFirebase} onChange={() => setShowFirebase(!showFirebase)}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}><Typography>Firebase Results</Typography></AccordionSummary>
            <AccordionDetails>
              {hasFirebase ? (
                Array.isArray(firebase) ? <DataTable data={firebase} title="Firebase" /> : <pre>{JSON.stringify(firebase, null, 2)}</pre>
              ) : (
                <Typography>No results.</Typography>
              )}
            </AccordionDetails>
          </Accordion>
        </Paper>
      )}

      {/* modification raw response */}
      {operation === 'modify' && result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Modification Results
          </Typography>
          <pre style={{ backgroundColor: '#f5f5f5', padding: '8px', overflowX: 'auto' }}>{result}</pre>
        </Paper>
      )}
    </Container>
  );
}

export default App;
