import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  Typography,
  Paper,
  Accordion,
  AccordionSummary,
  AccordionDetails
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import axios from 'axios';

function DataTable({ data, title }) {
  if (!data || data.length === 0) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No data found for {title}
      </Typography>
    );
  }

  // Collect all unique keys for table headers
  const allKeys = new Set();
  data.forEach((item) => {
    Object.keys(item).forEach((key) => allKeys.add(key));
  });
  const columns = Array.from(allKeys);

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Box sx={{ overflowX: 'auto', border: '1px solid #ddd' }}>
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              {columns.map((col) => (
                <th
                  key={col}
                  style={{
                    padding: '8px',
                    background: '#f0f0f0',
                    borderBottom: '1px solid #ccc',
                    textAlign: 'left'
                  }}
                >
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {columns.map((col) => (
                  <td
                    key={col}
                    style={{
                      padding: '8px',
                      borderBottom: '1px solid #eee',
                      verticalAlign: 'top'
                    }}
                  >
                    {row[col] !== null && row[col] !== undefined
                      ? String(row[col])
                      : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </Box>
    </Box>
  );
}

// Fields to display in merged results.
const MERGED_COLUMNS = [
  "id",
  "name",
  "price",
  "room_type",
  "listing_url",
  "description"
];

function filterColumns(dataArray, desiredFields) {
  if (!Array.isArray(dataArray)) return [];
  return dataArray.map(item => {
    const filtered = {};
    desiredFields.forEach(field => {
      if (item[field] !== undefined) {
        filtered[field] = item[field];
      }
    });
    return filtered;
  });
}

function App() {
  const [operation, setOperation] = useState('query'); // new state for operation type
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [showRawResults, setShowRawResults] = useState(true);
  const [showMySQL, setShowMySQL] = useState(true);
  const [showMongo, setShowMongo] = useState(true);
  const [showFirebase, setShowFirebase] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      let response;
      if (operation === 'query') {
        response = await axios.post('http://localhost:8000/query', {
          query,
          db_type: database
        });
      } else if (operation === 'modify') {
        // For modifications the endpoint and payload key is different.
        response = await axios.post('http://localhost:8000/modify', {
          modification: query
        });
      }
      setResult(JSON.stringify(response.data, null, 2));
    } catch (error) {
      setResult('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setResult('');
    setQuery('');
  };

  let parsedData;
  try {
    parsedData = result ? JSON.parse(result) : null;
  } catch (err) {
    parsedData = null;
  }

  // When operation is "query", the backend returns converted_queries and results.
  // When "modify", it returns converted_modifications and results.
  const converted = parsedData ? (operation === 'query' ? parsedData.converted_queries : parsedData.converted_modifications) : null;
  const results = parsedData ? parsedData.results : null;
  // For query, we are merging outputs as before.
  const { mysql, mongodb, firebase, merged } = results || {};

  const mergedFiltered = merged && Array.isArray(merged)
    ? filterColumns(merged, MERGED_COLUMNS)
    : [];

  const hasMySQL = mysql && Array.isArray(mysql) && mysql.length > 0;
  const hasMongoDB = mongodb && Array.isArray(mongodb) && mongodb.length > 0;
  const hasFirebase = firebase && (
    (Array.isArray(firebase) && firebase.length > 0) ||
    (typeof firebase === 'object' && Object.keys(firebase).length > 0)
  );

  // Final output for query is merged if available; otherwise falls back on one source.
  const finalOutput = mergedFiltered.length > 0
    ? mergedFiltered
    : hasFirebase
      ? firebase
      : hasMySQL
        ? mysql
        : hasMongoDB
          ? mongodb
          : [];

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Query/Modification Form Section */}
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          ChatDB - Natural Language Interface
        </Typography>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Operation Type</InputLabel>
              <Select
                value={operation}
                label="Operation Type"
                onChange={(e) => setOperation(e.target.value)}
              >
                <MenuItem value="query">Query</MenuItem>
                <MenuItem value="modify">Modification</MenuItem>
              </Select>
            </FormControl>
          </Box>

          {/* For modifications, the database choice may be less relevant */}
          {operation === 'query' && (
            <Box sx={{ mb: 2 }}>
              <FormControl fullWidth>
                <InputLabel>Database</InputLabel>
                <Select
                  value={database}
                  label="Database"
                  onChange={(e) => setDatabase(e.target.value)}
                >
                  <MenuItem value="mysql">MySQL</MenuItem>
                  <MenuItem value="mongodb">MongoDB</MenuItem>
                  <MenuItem value="firebase">Firebase</MenuItem>
                </Select>
              </FormControl>
            </Box>
          )}

          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label={operation === 'query' ? "Enter your query in natural language" : "Enter your modification command"}
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={operation === 'query'
                ? "e.g., Show me the listing with the highest number of reviews"
                : "e.g., Insert a new listing with id 123, name 'Test', price '$100.00', room_type 'Test Room', and host_id 1"}
            />
          </Box>

          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              variant="contained"
              type="submit"
              disabled={loading || !query}
              fullWidth
            >
              {loading ? 'Processing...' : (operation === 'query' ? 'Execute Query' : 'Execute Modification')}
            </Button>
            <Button
              variant="outlined"
              color="error"
              onClick={handleClear}
              disabled={loading || (!query && !result)}
              fullWidth
            >
              Clear All
            </Button>
          </Box>
        </form>
      </Paper>

      {/* For query operations, display final output first */}
      {operation === 'query' && result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Final Output (Merged or Single Source)
          </Typography>
          {finalOutput && finalOutput.length > 0 ? (
            <DataTable data={finalOutput} title="Final Output" />
          ) : (
            <Typography variant="body2">
              No final output available or no overlapping data between databases.
            </Typography>
          )}
        </Paper>
      )}

      {/* Toggle for Converted Queries */}
      {result && (
        <Box sx={{ mb: 2, display: 'flex', justifyContent: 'flex-end' }}>
          <Button
            variant="text"
            onClick={() => setShowRawResults(!showRawResults)}
          >
            {showRawResults ? 'Hide Converted Data' : 'Show Converted Data'}
          </Button>
        </Box>
      )}

      {result && showRawResults && converted && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            {operation === 'query' ? "Converted Queries" : "Converted Modification Commands"}
          </Typography>
          <pre style={{ backgroundColor: '#f5f5f5', padding: '8px', overflowX: 'auto' }}>
            {JSON.stringify(converted, null, 2)}
          </pre>
        </Paper>
      )}

      {operation === 'query' && result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Individual Database Results
          </Typography>
          <Accordion expanded={showMySQL} onChange={() => setShowMySQL(!showMySQL)}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography variant="subtitle1">MySQL Results</Typography>
            </AccordionSummary>
            <AccordionDetails>
              {hasMySQL ? (
                <DataTable data={mysql} title="MySQL" />
              ) : (
                <Typography variant="body2">No results from MySQL.</Typography>
              )}
            </AccordionDetails>
          </Accordion>
          <Accordion expanded={showMongo} onChange={() => setShowMongo(!showMongo)}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography variant="subtitle1">MongoDB Results</Typography>
            </AccordionSummary>
            <AccordionDetails>
              {hasMongoDB ? (
                <DataTable data={mongodb} title="MongoDB" />
              ) : (
                <Typography variant="body2">No results from MongoDB.</Typography>
              )}
            </AccordionDetails>
          </Accordion>
          <Accordion expanded={showFirebase} onChange={() => setShowFirebase(!showFirebase)}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography variant="subtitle1">Firebase Results</Typography>
            </AccordionSummary>
            <AccordionDetails>
              {hasFirebase ? (
                Array.isArray(firebase) ? (
                  <DataTable data={firebase} title="Firebase" />
                ) : (
                  <pre style={{ backgroundColor: '#f5f5f5', padding: '8px' }}>
                    {JSON.stringify(firebase, null, 2)}
                  </pre>
                )
              ) : (
                <Typography variant="body2">No results from Firebase.</Typography>
              )}
            </AccordionDetails>
          </Accordion>
        </Paper>
      )}

      {operation === 'modify' && result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Modification Results
          </Typography>
          <pre style={{ backgroundColor: '#f5f5f5', padding: '8px', overflowX: 'auto' }}>
            {result}
          </pre>
        </Paper>
      )}
    </Container>
  );
}

export default App;
