import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  Typography,
  Paper,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Tabs,
  Tab
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import axios from 'axios';

function DataTable({ data, title }) {
  if (!data || data.length === 0) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No data found for {title}
      </Typography>
    );
  }

  // Collect all unique keys for table headers
  const allKeys = new Set();
  data.forEach((item) => {
    Object.keys(item).forEach((key) => allKeys.add(key));
  });
  const columns = Array.from(allKeys);

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Box sx={{ overflowX: 'auto', border: '1px solid #ddd' }}>
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              {columns.map((col) => (
                <th
                  key={col}
                  style={{
                    padding: '8px',
                    background: '#f0f0f0',
                    borderBottom: '1px solid #ccc',
                    textAlign: 'left'
                  }}
                >
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {columns.map((col) => (
                  <td
                    key={col}
                    style={{
                      padding: '8px',
                      borderBottom: '1px solid #eee',
                      verticalAlign: 'top'
                    }}
                  >
                    {row[col] !== null && row[col] !== undefined
                      ? String(row[col])
                      : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </Box>
    </Box>
  );
}

// Component to display schema information
function SchemaDisplay({ data, title }) {
  if (!data || data.length === 0) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No schema information available for {title}
      </Typography>
    );
  }

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Box sx={{ overflowX: 'auto', border: '1px solid #ddd' }}>
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              {Object.keys(data[0]).map((col) => (
                <th
                  key={col}
                  style={{
                    padding: '8px',
                    background: '#f0f0f0',
                    borderBottom: '1px solid #ccc',
                    textAlign: 'left'
                  }}
                >
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {Object.entries(row).map(([key, value]) => (
                  <td
                    key={key}
                    style={{
                      padding: '8px',
                      borderBottom: '1px solid #eee',
                      verticalAlign: 'top'
                    }}
                  >
                    {value !== null && value !== undefined ? String(value) : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </Box>
    </Box>
  );
}

// Component to display Firebase/complex schema
function StructureDisplay({ data, title }) {
  if (!data) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No structure information available for {title}
      </Typography>
    );
  }

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Paper elevation={0} sx={{ p: 2, backgroundColor: '#f5f5f5' }}>
        <pre style={{ margin: 0, overflow: 'auto' }}>
          {JSON.stringify(data, null, 2)}
        </pre>
      </Paper>
    </Box>
  );
}

// Component to display a list of tables/collections/nodes
function ListDisplay({ data, title }) {
  if (!data || data.length === 0) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No items found for {title}
      </Typography>
    );
  }

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Box sx={{ border: '1px solid #ddd', p: 2 }}>
        <ul style={{ margin: 0, paddingLeft: 20 }}>
          {data.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </Box>
    </Box>
  );
}

// Fields to display in merged results.
const MERGED_COLUMNS = [
  "id",
  "name",
  "price",
  "room_type",
  "listing_url",
  "description"
];

function filterColumns(dataArray, desiredFields) {
  if (!Array.isArray(dataArray)) return [];
  return dataArray.map(item => {
    const filtered = {};
    desiredFields.forEach(field => {
      if (item[field] !== undefined) {
        filtered[field] = item[field];
      }
    });
    return filtered;
  });
}

function App() {
  const [operation, setOperation] = useState('query');
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [showRawResults, setShowRawResults] = useState(true);
  const [showResults, setShowResults] = useState(true);
  const [activeTab, setActiveTab] = useState(0); // For tabs in exploration view

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      let response;
      if (operation === 'query') {
        response = await axios.post('http://localhost:8000/query', {
          query,
          db_type: database
        });
      } else if (operation === 'explore') {
        response = await axios.post('http://localhost:8000/explore', {
          query,
          db_type: database
        });
      }
      setResult(JSON.stringify(response.data, null, 2));
    } catch (error) {
      setResult(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setResult('');
    setQuery('');
  };

  let parsedData;
  try {
    parsedData = result ? JSON.parse(result) : null;
  } catch (err) {
    parsedData = null;
  }

  // Parse different types of responses
  const isExploration = operation === 'explore' && parsedData && parsedData.exploration_type;
  const explorationData = isExploration ? parsedData : null;
  
  // Regular query response handling
  const converted = !isExploration && parsedData ? parsedData.converted_queries : null;
  const results = !isExploration && parsedData ? parsedData.results : null;
  const { mysql, mongodb, firebase, merged } = results || {};

  // Determine which database results to display based on selected database
  const databaseResults = database === 'mysql' ? mysql : 
                         database === 'mongodb' ? mongodb : 
                         database === 'firebase' ? firebase : [];
  
  const hasResults = Array.isArray(databaseResults) && databaseResults.length > 0;

  // Use merged results if available, otherwise use the specific database results
  const finalOutput = merged && Array.isArray(merged) && merged.length > 0 
    ? filterColumns(merged, MERGED_COLUMNS) 
    : databaseResults;

  // Render exploration content based on result type
  const renderExplorationContent = () => {
    if (!explorationData) return null;

    const { exploration_type, message, data, db_type } = explorationData;

    switch (exploration_type) {
      case 'tables':
      case 'collections':
      case 'nodes':
        return <ListDisplay data={data} title={message} />;
      
      case 'schema':
        if (db_type === 'firebase') {
          return <StructureDisplay data={data} title={message} />;
        }
        return <SchemaDisplay data={data} title={message} />;
      
      case 'sample_data':
        return <DataTable data={data} title={message} />;
      
      case 'error':
        return (
          <Typography color="error" variant="body1">
            {message}
          </Typography>
        );
      
      default:
        // If we get regular query results through exploration endpoint
        if (data && Array.isArray(data)) {
          return <DataTable data={data} title="Exploration Results" />;
        }
        return (
          <pre style={{ backgroundColor: '#f5f5f5', padding: '8px', overflowX: 'auto' }}>
            {JSON.stringify(explorationData, null, 2)}
          </pre>
        );
    }
  };

  const getExampleQueries = () => {
    switch (operation) {
      case 'explore':
        return [
          "What tables are in the database?",
          "Show me the schema of the Listings table",
          "What columns are in the Reviews table?",
          "Show me 5 sample rows from the Hosts table",
          "What collections are in MongoDB?",
          "Show me the structure of the Firebase listings node"
        ];
      case 'query':
      default:
        if (database === 'mongodb') {
          return [
            "Show me 3 listings in the Downtown neighborhood",
            "Count how many listings are in each neighborhood",
            "Show 3 listings sorted by host response rate",
            "Show 3 listings with their amenities details"
          ];
        } else if (database === 'firebase') {
          return [
            "Find listings with price less than $100",
            "Show me listings with high availability in the next 30 days",
            "Get the most expensive listings",
            "Find listings with cleaning fee less than $50"
          ];
        } else {
          return [
            "Show me listings with more than 50 reviews",
            "Find all listings with 2 or more bedrooms",
            "Get all hosts from New York",
            "Show me listings with high ratings"
          ];
        }
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Query/Exploration Form Section */}
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          ChatDB - Natural Language Interface
        </Typography>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Operation Type</InputLabel>
              <Select
                value={operation}
                label="Operation Type"
                onChange={(e) => setOperation(e.target.value)}
              >
                <MenuItem value="query">Query</MenuItem>
                <MenuItem value="explore">Explore Schema</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Database</InputLabel>
              <Select
                value={database}
                label="Database"
                onChange={(e) => setDatabase(e.target.value)}
              >
                <MenuItem value="mysql">MySQL</MenuItem>
                <MenuItem value="mongodb">MongoDB</MenuItem>
                <MenuItem value="firebase">Firebase</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label={operation === 'query' 
                ? "Enter your query in natural language" 
                : "Enter your schema exploration question"}
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={operation === 'query'
                ? "e.g., Show me the listings with the highest number of reviews"
                : "e.g., What tables are in the database? or Show me the schema of Listings table"}
            />
          </Box>

          <Box sx={{ mb: 1 }}>
            <Typography variant="body2" color="textSecondary">
              Example questions:
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 0.5 }}>
              {getExampleQueries().map((example, i) => (
                <Button 
                  key={i} 
                  size="small" 
                  variant="outlined" 
                  onClick={() => setQuery(example)}
                  sx={{ mb: 1 }}
                >
                  {example}
                </Button>
              ))}
            </Box>
          </Box>

          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              variant="contained"
              type="submit"
              disabled={loading || !query}
              fullWidth
            >
              {loading ? 'Processing...' : (operation === 'query' ? 'Execute Query' : 'Explore')}
            </Button>
            <Button
              variant="outlined"
              color="error"
              onClick={handleClear}
              disabled={loading || (!query && !result)}
              fullWidth
            >
              Clear All
            </Button>
          </Box>
        </form>
      </Paper>

      {/* Results Section */}
      {result && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            {isExploration ? 'Schema Exploration Results' : 'Query Results'}
          </Typography>

          {isExploration ? (
            renderExplorationContent()
          ) : (
            <>
              {/* For query operations, display final output first */}
              <Box sx={{ mb: 3 }}>
                <Typography variant="subtitle1" gutterBottom>
                  Final Output (Merged or Single Source)
                </Typography>
                {finalOutput && finalOutput.length > 0 ? (
                  <DataTable data={finalOutput} title="Final Output" />
                ) : (
                  <Typography variant="body2">
                    No results available or no overlapping data between databases.
                  </Typography>
                )}
              </Box>

              {/* Toggle for Converted Queries */}
              <Box sx={{ mb: 2, display: 'flex', justifyContent: 'flex-end' }}>
                <Button
                  variant="text"
                  onClick={() => setShowRawResults(!showRawResults)}
                >
                  {showRawResults ? 'Hide Converted Queries' : 'Show Converted Queries'}
                </Button>
              </Box>

              {showRawResults && converted && (
                <Box sx={{ mb: 3 }}>
                  <Typography variant="subtitle1" gutterBottom>
                    Converted Queries
                  </Typography>
                  <pre style={{ backgroundColor: '#f5f5f5', padding: '8px', overflowX: 'auto' }}>
                    {JSON.stringify(converted, null, 2)}
                  </pre>
                </Box>
              )}

              <Typography variant="subtitle1" gutterBottom>
                Database Results: {database.toUpperCase()}
              </Typography>
              
              <Accordion expanded={showResults} onChange={() => setShowResults(!showResults)}>
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  <Typography variant="subtitle1">{database.toUpperCase()} Results</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  {hasResults ? (
                    database === 'firebase' && !Array.isArray(databaseResults) ? (
                      <pre style={{ backgroundColor: '#f5f5f5', padding: '8px' }}>
                        {JSON.stringify(databaseResults, null, 2)}
                      </pre>
                    ) : (
                      <DataTable data={databaseResults} title={database.toUpperCase()} />
                    )
                  ) : (
                    <Typography variant="body2">No results from {database.toUpperCase()}.</Typography>
                  )}
                </AccordionDetails>
              </Accordion>
            </>
          )}
        </Paper>
      )}
    </Container>
  );
}

export default App;
