import React, { useState } from 'react';
import { 
  Container, 
  TextField, 
  Button, 
  Select, 
  MenuItem, 
  FormControl, 
  InputLabel, 
  Box, 
  Typography,
  Paper,
  Switch,
  FormControlLabel
} from '@mui/material';
import axios from 'axios';
import { mockResponse } from './mockData';

function App() {
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [isTestMode, setIsTestMode] = useState(true);  // 默认使用测试模式

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (isTestMode) {
        // 使用模拟数据
        setTimeout(() => {
          setResult(JSON.stringify(mockResponse, null, 2));
          setLoading(false);
        }, 500);  // 模拟网络延迟
      } else {
        // 实际 API 调用
        const response = await axios.post('http://localhost:5000/query', {
          query,
          db_type: database
        }, {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: false
        });
        setResult(JSON.stringify(response.data, null, 2));
      }
    } catch (error) {
      setResult('Error: ' + error.message);
    }
    setLoading(false);
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        ChatDB - Natural Language Database Interface
      </Typography>

      <FormControlLabel
        control={
          <Switch
            checked={isTestMode}
            onChange={(e) => setIsTestMode(e.target.checked)}
          />
        }
        label="Test Mode"
        sx={{ mb: 2 }}
      />

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Database</InputLabel>
              <Select
                value={database}
                label="Database"
                onChange={(e) => setDatabase(e.target.value)}
              >
                <MenuItem value="mysql">MySQL</MenuItem>
                <MenuItem value="firebase">Firebase</MenuItem>
                <MenuItem value="mongodb">MongoDB</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Enter your query in natural language"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="e.g., Show me all users who made purchases in the last month"
            />
          </Box>

          <Button 
            variant="contained" 
            type="submit" 
            disabled={loading || !query}
            fullWidth
          >
            {loading ? 'Processing...' : 'Execute Query'}
          </Button>
        </form>
      </Paper>

      {result && (
        <Paper elevation={3} sx={{ p: 3 }}>
          <Typography variant="h6" gutterBottom>
            Results:
          </Typography>
          <pre style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
            {result}
          </pre>
        </Paper>
      )}
    </Container>
  );
}

export default App;
