import React, { useState } from 'react';
import { 
  Container, 
  TextField, 
  Button, 
  Select, 
  MenuItem, 
  FormControl, 
  InputLabel, 
  Box, 
  Typography,
  Paper
} from '@mui/material';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/query', {
        query,
        db_type: database
      });
      setResult(JSON.stringify(response.data, null, 2));
    } catch (error) {
      setResult('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setResult('');
    setQuery('');
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        ChatDB - Natural Language Database Interface
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Database</InputLabel>
              <Select
                value={database}
                label="Database"
                onChange={(e) => setDatabase(e.target.value)}
              >
                <MenuItem value="mysql">MySQL</MenuItem>
                <MenuItem value="firebase">Firebase</MenuItem>
                <MenuItem value="mongodb">MongoDB</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Enter your query in natural language"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="e.g., Show me all listings under $150"
            />
          </Box>

          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button 
              variant="contained" 
              type="submit" 
              disabled={loading || !query}
              fullWidth
            >
              {loading ? 'Processing...' : 'Execute Query'}
            </Button>
            <Button
              variant="outlined"
              color="error"
              onClick={handleClear}
              disabled={loading || (!query && !result)}
            >
              Clear All
            </Button>
          </Box>
        </form>
      </Paper>

      {result && (
        <Paper elevation={3} sx={{ p: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6">
              Results:
            </Typography>
            <Button
              size="small"
              variant="outlined"
              color="error"
              onClick={() => setResult('')}
            >
              Clear Results
            </Button>
          </Box>
          <pre style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
            {result}
          </pre>
        </Paper>
      )}
    </Container>
  );
}

export default App;
