import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  Typography,
  Paper,
  Grid
} from '@mui/material';
import axios from 'axios';

function DataTable({ data, title }) {
  if (!data || data.length === 0) {
    return (
      <Typography variant="subtitle1" gutterBottom>
        No data found for {title}
      </Typography>
    );
  }

  // Collect a set of unique keys across all rows for table headers
  const allKeys = new Set();
  data.forEach((item) => {
    Object.keys(item).forEach((key) => allKeys.add(key));
  });
  const columns = Array.from(allKeys);

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <table border="1" cellPadding="6" style={{ borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {columns.map((col) => (
                <td key={col}>
                  {row[col] !== null && row[col] !== undefined ? String(row[col]) : ''}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </Box>
  );
}

// Helper to filter out only the columns we want in Merged results
const MERGED_COLUMNS = [
  "id",
  "name",
  "price",
  "room_type",
  "listing_url",
  "description"
];

function filterColumns(dataArray, desiredFields) {
  if (!Array.isArray(dataArray)) return [];
  return dataArray.map(item => {
    const filtered = {};
    desiredFields.forEach(field => {
      if (item[field] !== undefined) {
        filtered[field] = item[field];
      }
    });
    return filtered;
  });
}

function App() {
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [showRawResults, setShowRawResults] = useState(true); // NEW: Toggle state for raw results

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/query', {
        query,
        db_type: database
      });
      setResult(JSON.stringify(response.data, null, 2));
    } catch (error) {
      setResult('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setResult('');
    setQuery('');
  };

  // Try to parse result JSON if it exists
  let parsedData;
  try {
    parsedData = result ? JSON.parse(result) : null;
  } catch (err) {
    parsedData = null;
  }

  const { converted_queries, results } = parsedData || {};
  const { mysql, mongodb, firebase, merged } = results || {};

  // Filter down merged data if it exists
  const mergedFiltered = filterColumns(merged, MERGED_COLUMNS);

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* 1. Query Section */}
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          ChatDB - Natural Language Interface
        </Typography>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Database</InputLabel>
              <Select
                value={database}
                label="Database"
                onChange={(e) => setDatabase(e.target.value)}
              >
                <MenuItem value="mysql">MySQL</MenuItem>
                <MenuItem value="mongodb">MongoDB</MenuItem>
                <MenuItem value="firebase">Firebase</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Enter your query in natural language"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="e.g., Show me all listings under $150..."
            />
          </Box>

          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              variant="contained"
              type="submit"
              disabled={loading || !query}
              fullWidth
            >
              {loading ? 'Processing...' : 'Execute Query'}
            </Button>
            <Button
              variant="outlined"
              color="error"
              onClick={handleClear}
              disabled={loading || (!query && !result)}
            >
              Clear All
            </Button>
          </Box>
        </form>
      </Paper>

      {/* Toggle Button for Raw Query Results */}
      {result && (
        <Box sx={{ mb: 2, display: 'flex', justifyContent: 'flex-end' }}>
          <Button
            variant="text"
            onClick={() => setShowRawResults(!showRawResults)}
          >
            {showRawResults ? 'Hide Raw Results' : 'Show Raw Results'}
          </Button>
        </Box>
      )}

      {/* 2. Results Section (Raw results displayed only if toggled to show) */}
      {result && showRawResults && (
        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Typography variant="h5" gutterBottom>
            Query & Raw Results
          </Typography>

          {/* A. Converted Queries */}
          {converted_queries && (
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1">Converted Queries</Typography>
              <pre style={{ backgroundColor: '#f5f5f5', padding: '8px' }}>
                {JSON.stringify(converted_queries, null, 2)}
              </pre>
            </Box>
          )}

          {/* B. MySQL, MongoDB, Firebase Results */}
          {mysql && Array.isArray(mysql) && (
            <DataTable data={mysql} title="MySQL Results" />
          )}
          {mongodb && Array.isArray(mongodb) && (
            <DataTable data={mongodb} title="MongoDB Results" />
          )}
          {firebase && (
            Array.isArray(firebase) ? (
              <DataTable data={firebase} title="Firebase Results" />
            ) : (
              <Box sx={{ mb: 2 }}>
                <Typography variant="subtitle1">Firebase Results</Typography>
                <pre style={{ backgroundColor: '#f5f5f5', padding: '8px' }}>
                  {JSON.stringify(firebase, null, 2)}
                </pre>
              </Box>
            )
          )}
        </Paper>
      )}

      {/* 3. Merged Final Output */}
      {mergedFiltered && mergedFiltered.length > 0 && (
        <Paper elevation={3} sx={{ p: 3 }}>
          <Typography variant="h5" gutterBottom>
            Merged Final Output
          </Typography>
          <DataTable data={mergedFiltered} title="Merged Results (Filtered)" />
        </Paper>
      )}
    </Container>
  );
}

export default App;
