import React, { useState } from 'react';
import { 
  Container, 
  TextField, 
  Button, 
  Select, 
  MenuItem, 
  FormControl, 
  InputLabel, 
  Box, 
  Typography,
  Paper,
  Switch,
  FormControlLabel,
  IconButton,
  Tooltip
} from '@mui/material';
import axios from 'axios';
import { mockResponse } from './mockData';

function App() {
  const [query, setQuery] = useState('');
  const [database, setDatabase] = useState('mysql');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [isTestMode, setIsTestMode] = useState(true); // 默认使用测试模式

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (isTestMode) {
        // 使用模拟数据
        await new Promise(resolve => setTimeout(resolve, 500)); // 模拟网络延迟
        setResult(JSON.stringify(mockResponse, null, 2));
      } else {
        // 实际 API 调用
        const response = await axios.post('http://localhost:5000/query', {
          query,
          db_type: database
        });
        setResult(JSON.stringify(response.data, null, 2));
      }
    } catch (error) {
      setResult('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleTestQuery = (predefinedQuery) => {
    setQuery(predefinedQuery);
  };

  const handleClear = () => {
    setResult('');
    setQuery('');
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        ChatDB - Natural Language Database Interface
      </Typography>

      {/* 测试模式开关 */}
      <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 2 }}>
        <FormControlLabel
          control={
            <Switch
              checked={isTestMode}
              onChange={(e) => setIsTestMode(e.target.checked)}
            />
          }
          label="Test Mode"
        />
        {isTestMode && (
          <Typography variant="body2" color="text.secondary">
            Using mock data for testing
          </Typography>
        )}
      </Box>

      {/* 测试查询按钮 */}
      {isTestMode && (
        <Box sx={{ mb: 2 }}>
          <Typography variant="subtitle2" gutterBottom>
            Test Queries:
          </Typography>
          <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
            <Button 
              size="small" 
              variant="outlined"
              onClick={() => handleTestQuery("Show me all listings under $150")}
            >
              Price Query
            </Button>
            <Button 
              size="small" 
              variant="outlined"
              onClick={() => handleTestQuery("Find cozy apartments with good reviews")}
            >
              Text Search
            </Button>
            <Button 
              size="small" 
              variant="outlined"
              onClick={() => handleTestQuery("Show available listings next week")}
            >
              Availability
            </Button>
          </Box>
        </Box>
      )}

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <form onSubmit={handleSubmit}>
          <Box sx={{ mb: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Database</InputLabel>
              <Select
                value={database}
                label="Database"
                onChange={(e) => setDatabase(e.target.value)}
              >
                <MenuItem value="mysql">MySQL</MenuItem>
                <MenuItem value="firebase">Firebase</MenuItem>
                <MenuItem value="mongodb">MongoDB</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Enter your query in natural language"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="e.g., Show me all listings under $150"
            />
          </Box>

          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button 
              variant="contained" 
              type="submit" 
              disabled={loading || !query}
              fullWidth
            >
              {loading ? 'Processing...' : 'Execute Query'}
            </Button>
            <Button
              variant="outlined"
              color="error"
              onClick={handleClear}
              disabled={loading || (!query && !result)}
            >
              Clear All
            </Button>
          </Box>
        </form>
      </Paper>

      {result && (
        <Paper elevation={3} sx={{ p: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6">
              Results:
            </Typography>
            <Button
              size="small"
              variant="outlined"
              color="error"
              onClick={() => setResult('')}
            >
              Clear Results
            </Button>
          </Box>
          <pre style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
            {result}
          </pre>
        </Paper>
      )}
    </Container>
  );
}

export default App;
